refsection.programlisting:
// Sample data v = [ .. 9.999; .. 9.998; .. 10.002; .. 10.000; .. 10.001; .. 10.000 .. ];
// Statistical confidence level p = "95%";
// Calculate statistical results n = length(v); // Number of values x = mean(v); // Arithmetic mean s = stdev (v); // Standard deviation (S.D.) sa = ST_strayarea(v, p); // Range of dispersion of the values (stray area) ta = ST_trustarea(v, p); // Range of dispersion of the mean (trust area) mi = min(v); // Minimal value ma = max(v); // Maximal value
// Output clc; mprintf("
SampleStatDemo - Demo script for toolbox_samplestat

"); mprintf("Values:
"); disp(v); // Display data mprintf("
"); // blank line mprintf(.. "Number of Values : %i
" + .. "Arithmetic Mean : %f
" + .. "Standard Deviation (S.D.) : %f
" + .. "Confidence Level : %s
" + .. "Range of Dispersion (values): %f
" + .. "Range of Dispersion (mean) : %f
" + .. "Minimum : %f
" + .. "Maximum : %f
", .. n, x, s, p, sa, ta, mi, ma); mprintf("
"); // blank line
mprintf( .. "68 percent of the values will stray arount %.3f +- %.3f (S.D.). %s of the values
" + .. "will be expected around %.3f +/- %.3f (Range of disp. of the values, stray area).
" + .. "With a propability of %s the mean of %.3f will stray around %.3f +/- %.3f (Rage of 
" + .. "dispersion of the mean, trust area).
", x, s, p, x, sa, p, x, x, ta);
